mapper.select:
select a.ID, a.IMAGE_NAME, 'imageAlbum' || '/' || a.IMAGE_PATH AS IMAGE_PATH, 'imageAlbumThumbanil' || '/' || a.IMAGE_PATH AS imageTemp, a.FILE_TYPE, a.SESSION_YEAR, a.SESSION_MONTH, a.SESSION_PIOVINCE, a.SESSION_REGION, a.CREATE_DATE, b.PSN_NAME AS CREATE_ID, a.IS_LIKE, CASE WHEN a.IS_LIKE = 0 THEN '否' ELSE '已收藏' END AS isLikeName from OPEN_IMAGE_ALBUM a LEFT JOIN SYS_PERSON b ON a.CREATE_ID = b.ID where a.del_flag = '0' AND a.CREATE_ID = #{getImageAlbumVo.createId}
AND a.IMAGE_NAME LIKE '%' || #{getImageAlbumVo.imageName} || '%'
AND a.SESSION_YEAR = #{getImageAlbumVo.sessionYear}
AND a.SESSION_MONTH = #{getImageAlbumVo.sessionMonth}
AND a.SESSION_PIOVINCE LIKE '%' || #{getImageAlbumVo.sessionPiovince} || '%'
AND a.SESSION_REGION LIKE '%' || #{getImageAlbumVo.sessionRegion} || '%'
AND a.IS_LIKE = #{getImageAlbumVo.isLike}
ORDER BY TO_NUMBER(a.SESSION_YEAR) DESC, TO_NUMBER(a.SESSION_MONTH) DESC
