mapper.select:
SELECT C.ID, A.ACT_ID, A.CLOB_TITLE, A.CREATE_ID, A.CREATE_DATE, A.UPDATE_DATE, A.DEL_FLAG, A.ON_STATE, B.ID AS "psnId", B.PSN_NAME AS "psnName", D.BLOS_TYPE_NAME AS actName FROM SYS_MINE_ACTIVITY A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID LEFT JOIN SYS_ACTIVITY C ON C.ID = A.ACT_ID LEFT JOIN SYS_BLOS_TYPE D ON D.ID = C.ACT_TYPE WHERE A.DEL_FLAG = '0'
AND A.ID = #{mineBlos.ids}
AND A.ACT_ID = #{mineBlos.actId}
AND A.CLOB_TITLE LIKE '%'|| #{mineBlos.clobTitle} || '%'
AND A.CREAYE_ID = #{mineBlos.createId}
AND A.PSN_ID = #{mineBlos.psnId}
AND C.ACT_TYPE = #{mineBlos.actType}
ORDER BY A.CREATE_DATE DESC

SELECT * FROM( SELECT A.ID, A.FILE_NAME AS "CLOB_TITLE", A.CREATE_DATE, A.CREATE_ID, A.UPDATE_DATE, A.DEL_FLAG, B.ID AS "psnId", B.PSN_NAME AS "psnName" FROM SYS_FILE A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID WHERE A.DEL_FLAG = '0'
AND A.ID = #{mineBlos.ids}
AND A.FILE_NAME LIKE '%'|| #{mineBlos.clobTitle} || '%'
AND A.CREAYE_ID = #{mineBlos.createId}
AND A.PSN_ID = #{mineBlos.psnId}
ORDER BY CREATE_DATE DESC ) WHERE ROWNUM <= 100

SELECT A.ID, C.CLOB_TITLE AS "CLOB_TITLE", A.CREATE_DATE, A.CREATE_ID, A.DEL_FLAG, B.ID AS "psnId", B.PSN_NAME AS "psnName" FROM SYS_ACTIVITY_LIKE A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID LEFT JOIN SYS_MINE_ACTIVITY C ON C.ID = A.MINE_ACTIVITY_ID WHERE A.DEL_FLAG = '0'
AND A.ID = #{mineBlos.ids}
AND C.CLOB_TITLE LIKE '%'|| #{mineBlos.clobTitle} || '%'
AND A.CREAYE_ID = #{mineBlos.createId}
AND A.PSN_ID = #{mineBlos.psnId}
ORDER BY A.CREATE_DATE DESC

SELECT
	COUNT(A.ID)
FROM
	SYS_MINE_ACTIVITY A
WHERE
	A.DEL_FLAG = '0'

	AND A.ID = #{mineBlos.ids}


	AND A.ACT_ID = #{mineBlos.actId}


	AND A.CLOB_TITLE LIKE '%'|| #{mineBlos.clobTitle} || '%'


	AND A.CREAYE_ID = #{mineBlos.createId}


	AND A.PSN_ID = #{mineBlos.psnId}

SELECT * FROM( SELECT A.ID, A.ACT_ID, A.CLOB_TITLE, A.CREATE_ID, A.CREATE_DATE, A.UPDATE_DATE, A.DEL_FLAG, A.ON_STATE, B.ID AS "psnId", B.PSN_NAME AS "psnName" FROM SYS_MINE_ACTIVITY A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID WHERE A.DEL_FLAG = '0'
AND A.PSN_ID = #{mineBlos.psnId}
AND TO_DATE(A.UPDATE_DATE,'yyyy-mm-dd hh24:mi:ss') >= SYSDATE-2 UNION ALL SELECT A.ID, NULL, A.FILE_NAME AS "CLOB_TITLE", A.CREATE_ID, A.CREATE_DATE, A.UPDATE_DATE, A.DEL_FLAG, NULL, B.ID AS "psnId", B.PSN_NAME AS "psnName" FROM SYS_FILE A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID WHERE A.DEL_FLAG = '0'
AND A.PSN_ID = #{mineBlos.psnId}
AND TO_DATE(A.UPDATE_DATE,'yyyy-mm-dd hh24:mi:ss') >= SYSDATE-2 )ORDER BY CREATE_DATE DESC

mapper.insert:
declare
v_clob clob := #{mineBlos.content};
begin
INSERT INTO SYS_MINE_ACTIVITY(ID,ACT_ID,CONTENT,SIML_CONTENT,CLOB_TITLE,CREATE_ID,CREATE_DATE,UPDATE_DATE,DEL_FLAG,ON_STATE,PSN_ID,CENT_IMG)
VALUES(#{mineBlos.ids},#{mineBlos.actId},v_clob,#{mineBlos.liteCont},#{mineBlos.clobTitle},#{mineBlos.createId},#{mineBlos.createDate},#{mineBlos.updateDate}
	  ,'0',#{mineBlos.onState},#{mineBlos.psnId},#{mineBlos.centImg});
end;

mapper.update:
declare
v_clob clob := #{mineBlos.content};
begin
UPDATE SYS_MINE_ACTIVITY SET CONTENT = v_clob, SIML_CONTENT = #{mineBlos.liteCont}, CLOB_TITLE = #{mineBlos.clobTitle}, UPDATE_DATE = #{mineBlos.updateDate}
WHERE ACT_ID = #{mineBlos.actId};
end;

mapper.select:
SELECT A.ID, A.ACT_ID, A.CLOB_TITLE, A.CREATE_ID, A.CREATE_DATE, A.UPDATE_DATE, A.DEL_FLAG, A.ON_STATE, A.CONTENT, D.ID AS "psnId", D.PSN_NAME AS "psnName", C.LIKE_NAME AS "type", E.BLOS_TYPE_NAME AS "ACT_TYPE", 'blosBoot' || A.CENT_IMG as "CENT_IMG" FROM SYS_MINE_ACTIVITY A LEFT JOIN SYS_ACTIVITY B ON B.ID = A.ACT_ID LEFT JOIN SYS_LIKE C ON C.ID = B.ACT_LIKE LEFT JOIN SYS_PERSON D ON D.ID = A.CREATE_ID LEFT JOIN SYS_BLOS_TYPE E ON B.ACT_TYPE = E.ID WHERE A.DEL_FLAG = '0' AND B.DEL_FLAG = '0'
AND B.STATE = #{mineBlos.onState}
AND A.ID = #{mineBlos.ids}
AND A.ACT_ID = #{mineBlos.actId}

SELECT A.ID, A.ACT_ID, A.SIML_CONTENT, A.CLOB_TITLE, A.CREATE_ID, A.CREATE_DATE, A.UPDATE_DATE, B.ID AS "psnId", B.PSN_NAME AS "psnName", D.BLOS_TYPE_NAME AS "ACT_TYPE", 'blosBoot' || A.CENT_IMG as "CENT_IMG" FROM SYS_MINE_ACTIVITY A LEFT JOIN SYS_PERSON B ON B.ID = A.PSN_ID LEFT JOIN SYS_ACTIVITY C ON C.ID = A.ACT_ID LEFT JOIN SYS_BLOS_TYPE D ON D.ID = C.ACT_TYPE WHERE A.DEL_FLAG = '0'
AND C.STATE = #{mineBlos.onState}
AND A.ID = #{mineBlos.ids}
AND A.CLOB_TITLE LIKE '%'|| #{mineBlos.clobTitle} || '%'
AND A.PSN_ID = #{mineBlos.psnId}
AND C.ACT_TYPE = #{mineBlos.actType}
AND C.CREATE_DATE LIKE #{mineBlos.createDate} || '%'
ORDER BY CREATE_DATE DESC

SELECT * FROM( SELECT #{type} || ID AS ID, CLOB_TITLE FROM SYS_MINE_ACTIVITY WHERE DEL_FLAG = '0'
AND ID != #{ids}
ORDER BY CREATE_DATE DESC) WHERE rownum < 5
