mapper.select:
SELECT * FROM SYS_DIC_CONFIG WHERE IS_TEST = #{sysConfig.isTest} AND DEL_FLAG = 0
AND DIC_KEY LIKE '%' || #{sysConfig.dicKey} || '%'
AND DIC_VALUE LIKE '%' || #{sysConfig.dicValue} || '%'
AND IS_STATUS = || #{sysConfig.isStatus}
AND IS_SYSTEM = #{sysConfig.isSystem}

SELECT ID, DIC_KEY, DIC_VALUE, CASE WHEN IS_STATUS = '1' THEN '启用中' ELSE '未启用' END AS IS_STATUS, IS_SYSTEM, REMARK, IS_TEST FROM SYS_DIC_CONFIG
DEL_FLAG = 0
AND IS_TEST = #{sysConfig.isTest}
AND DIC_KEY LIKE '%' || #{sysConfig.dicKey} || '%'
AND DIC_VALUE LIKE '%' || #{sysConfig.dicValue} || '%'
AND IS_STATUS = || #{sysConfig.isStatus}
AND IS_SYSTEM = #{sysConfig.isSystem}
ORDER BY CREATE_DATE DESC
